@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);
  --opaque-button-border-intensity: -8;
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);
  --background: 210 40% 99%;
  --foreground: 210 25% 8%;
  --border: 210 15% 87%;
  --card: 0 0% 100%;
  --card-foreground: 210 25% 8%;
  --card-border: 210 15% 87%;
  --sidebar: 210 16% 94%;
  --sidebar-foreground: 210 15% 16%;
  --sidebar-border: 210 12% 86%;
  --sidebar-primary: 195 92% 38%;
  --sidebar-primary-foreground: 195 92% 98%;
  --sidebar-accent: 210 14% 88%;
  --sidebar-accent-foreground: 210 15% 18%;
  --sidebar-ring: 195 88% 42%;
  --popover: 0 0% 100%;
  --popover-foreground: 210 25% 8%;
  --popover-border: 210 15% 87%;
  --primary: 195 92% 32%;
  --primary-foreground: 195 92% 98%;
  --secondary: 210 14% 86%;
  --secondary-foreground: 210 15% 20%;
  --muted: 210 15% 92%;
  --muted-foreground: 210 10% 42%;
  --accent: 210 14% 88%;
  --accent-foreground: 210 15% 22%;
  --destructive: 358 85% 28%;
  --destructive-foreground: 358 85% 98%;
  --input: 210 15% 68%;
  --ring: 195 88% 42%;
  --chart-1: 195 88% 32%;
  --chart-2: 162 75% 28%;
  --chart-3: 280 65% 32%;
  --chart-4: 32 85% 32%;
  --chart-5: 220 75% 32%;
  --bullish: 160 100% 38%;
  --bullish-foreground: 0 0% 100%;
  --bearish: 358 75% 56%;
  --bearish-foreground: 0 0% 100%;
  --neutral: 210 10% 52%;
  --neutral-foreground: 0 0% 100%;
  --spike: 192 100% 42%;
  --spike-foreground: 0 0% 100%;
  --font-sans: DM Sans, Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: JetBrains Mono, Roboto Mono, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(210 12% 88% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(210 12% 88% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(210 12% 88% / 0.00), 0px 1px 2px -1px hsl(210 12% 82% / 0.05);
